import React from "react";
import { Card } from "antd";
import "./ItemPhim.css";
const { Meta } = Card;
export default function ItemPhim({ phim }) {
  return (
    // <div className="mr-3 mb-3">
    //   <Card
    //     hoverable
    //     style={{ width: "100%", height: "600px" }}
    //     cover={
    //       <div>
    //         <img
    //           alt="example"
    //           src={phim.hinhAnh}
    //           style={{ width: "100%", height: "400px", objectFit: "cover" }}
    //         />
    //       </div>
    //     }
    //   >
    //     <Meta title={phim.tenPhim} description={phim.ngayKhoiChieu} />
    //   </Card>
    // </div>
    <div className="flip-card mt-3 ">
      <div className="flip-card-inner">
        <div className="flip-card-front">
          <img
            src={phim.hinhAnh}
            alt="Avatar"
            style={{ width: "100%", height: "100%", objectFit: "cover" }}
          />
        </div>
        <div className="flip-card-back">
          <div>
            <button
              type="button"
              className="px-8 py-3 font-semibold border rounded dark:border-coolGray-100 dark:text-coolGray-100 mb-3 mt-10"
              style={{ width: "180px" }}
            >
              ĐẶT VÉ
            </button>
          </div>
          <div>
            <button
              type="button"
              className="px-8 py-3 font-semibold border rounded bg-black-800 dark:text-white mb-5"
              style={{ width: "180px" }}
            >
              XEM CHI TIẾT
            </button>
            <h1 className="text-2xl">{phim.tenPhim}</h1>
          </div>
        </div>
      </div>
    </div>
  );
}
