import React, { useEffect } from "react";

import { Carousel } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { getCarouselAction } from "../../../redux/actions/carouselAction";
import axios from "axios";
import { DOMAIN, TOKEN_CYBERSOFT } from "../../../configURL/constantURL";

export default function HomeCarousel() {
  const dispatch = useDispatch();
  const { arrCarousel } = useSelector((state) => state.carouselReducer);
  useEffect(() => {
    // dispatch(getCarouselAction());
    axios({
      url: DOMAIN + "/api/QuanLyPhim/LayDanhSachBanner",
      method: "GET",
      headers: TOKEN_CYBERSOFT,
    })
      .then((res) => {
        console.log("res", res);
        dispatch({
          type: "SET_CAROUDEL",
          payload: res.data.content,
        });
      })
      .catch((err) => {
        console.log("err", err);
      });
  }, []);

  const contentStyle = {
    height: "100%",
    color: "#fff",
    lineHeight: "160px",
    textAlign: "center",
    background: "#364d79",
  };

  const renderCarousel = () => {
    return arrCarousel.map((item, index) => {
      return (
        <div key={index}>
          <h3 style={contentStyle}>
            <img className="w-full" src={item.hinhAnh} alt="123" />
          </h3>
        </div>
      );
    });
  };
  return <Carousel autoplay>{renderCarousel()}</Carousel>;
}
