import React, { Component } from "react";
import Slider from "react-slick";
import ItemPhim from "../../pages/Home/ItemPhim/ItemPhim";
import styleSlick from "./MultipleRowSlick.module.css";

function SampleNextArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} ${styleSlick["slick-next"]}`}
      style={{ ...style, display: "block" }}
      onClick={onClick}
    />
  );
}
function SamplePrevArrow(props) {
  const { className, style, onClick } = props;
  return (
    <div
      className={`${className} ${styleSlick["slick-prev"]}`}
      style={{ ...style, display: "block" }}
      onClick={onClick}
    />
  );
}

export default class MultipleRows extends Component {
  renderPhim = () => {
    return this.props.arrFilm?.slice(0, 10).map((phim, index) => {
      return (
        <div className={`${styleSlick["width-item"]}`} key={index}>
          <ItemPhim phim={phim} />
        </div>
      );
    });
  };
  render() {
    const settings = {
      className: "center variable-width",
      centerMode: true,
      infinite: true,
      centerPadding: "60px",
      slidesToShow: 3,
      speed: 500,
      rows: 1,
      slidesPerRow: 2,
      variableWidth: true,
      nextArrow: <SampleNextArrow />,
      prevArrow: <SamplePrevArrow />,
    };
    return (
      <div>
        <button
          type="button"
          className="px-8 py-3 font-semibold border rounded bg-black text-white mr-2"
        >
          Phim đang chiếu
        </button>
        <button
          type="button"
          className="px-8 py-3 font-semibold border rounded bg-black text-white "
        >
          Phim sắp chiếu
        </button>
        <Slider {...settings}>{this.renderPhim()}</Slider>
      </div>
    );
  }
}
