import React, { Fragment, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { layChiTietPhongVeAction } from "../../redux/actions/quanLyDatVeAction";
import "./TicketBookingPage.css";
import { CloseOutlined } from "@ant-design/icons";

const App = () => (
  <Space>
    <SmileTwoTone />
    <HeartTwoTone twoToneColor="#eb2f96" />
    <CheckCircleTwoTone twoToneColor="#52c41a" />
  </Space>
);

export default function TicketBookingPage() {
  const { userInfor } = useSelector((state) => state.quanLyNguoiDungReducer);

  const { maLichChieu } = useParams();

  const { chiTietPhongVe } = useSelector((state) => state.quanLyDatVeReducer);

  console.log("chiTietPhongVe", chiTietPhongVe);
  const { thongTinPhim, danhSachGhe } = chiTietPhongVe;

  const renderSeats = () => {
    return danhSachGhe.map((ghe, index) => {
      let classgheVIP = ghe.loaiGhe === "Vip" ? "gheVip" : "";
      let classgheDaDat = ghe.daDat === true ? "gheDaDat" : "";
      return (
        <Fragment key={index}>
          <button
            disabled={ghe.daDat}
            className={`ghe ${classgheVIP} ${classgheDaDat}`}
          >
            {ghe.daDat ? (
              <CloseOutlined style={{ marginBottom: 6 }} />
            ) : (
              ghe.stt
            )}
          </button>
          {(index + 1) % 16 === 0 ? <br /> : ""}
        </Fragment>
      );
    });
  };

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(layChiTietPhongVeAction(maLichChieu));
  }, []);
  return (
    <div className="m-5 min-h-screen">
      <div className="grid grid-cols-12 ">
        <div className="col-span-9 mt-5">
          <div className="flex justify-center">
            <div>
              <img
                src="https://movie-booking-project.vercel.app/img/bookticket/screen.png"
                alt="man-hinh"
              />
            </div>
          </div>
          <div className="flex justify-center">
            <div>{renderSeats()}</div>
          </div>
        </div>
        <div className="min-h-screen col-span-3 flex flex-col justify-between">
          <div className="">
            <div className="text-center">
              <h1 className="text-green-600 text-4xl font-bold">0 Đ</h1>
            </div>
            <hr />
            <div>
              <h3 className="text-xl text-black font-bold">
                {thongTinPhim.tenPhim}
              </h3>
              <p>
                Địa điểm: {thongTinPhim.diaChi} - {thongTinPhim.tenCumRap}
              </p>
              <p>
                Ngày chiếu: {thongTinPhim.ngayChieu} - {thongTinPhim.gioChieu}{" "}
                {thongTinPhim.tenRap}
              </p>
            </div>
            <hr />
            <div className="flex">
              <h3 className="w-4/5 text-red-600 text-xl">Ghế</h3>
              <p className="text-green-600 text-xl">0 Đ</p>
            </div>
            <hr />
            <div>
              <i className="text-gray-600">Email</i>
              <p className="text-black font-semibold">{userInfor.email}</p>
            </div>
            <hr />
            <div>
              <i className="text-gray-600">Phone</i>
              <p className="text-black font-semibold">{userInfor.soDT}</p>
            </div>
          </div>
          <hr />

          <div className="mb-10 ">
            <div className="w-full bg-green-600 text-white text-center text-2xl p-3">
              ĐẶT VÉ
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
