import React from "react";
import moment from "moment";
export default function ItemTabsPhim({ phim }) {
  return (
    <div className="flex">
      <div>
        <img
          src={phim.hinhAnh}
          alt={phim.tenPhim}
          style={{ width: "75px", height: "75px", objectFit: "cover" }}
          className="my-2 rounded "
        />
        <div className="ml-3">
          <h2 className="font-bold text-green-600">{phim.tenPhim}</h2>
          <div className="grid grid-cols-4 gap-3">
            {phim.lstLichChieuTheoPhim?.slice(0, 10).map((lichChieu, index) => {
              return (
                <button
                  key={index}
                  className="text-green-700 border-2 border-green-400 rounded"
                >
                  {moment(lichChieu.ngayChieuGioChieu).format("DD-MM-YYYY")}
                </button>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
}
