import { history } from "../../App";
import QuanLyNguoiDungServ from "../../services/QuanLyNguoiDungservice";
import { Alert, Button, message, Result } from "antd";

export const dangKyAction = (data) => {
  return (dispatch) => {
    QuanLyNguoiDungServ.dangKy(data)
      .then((res) => {
        // console.log("res", res);
        message.success("Đăng ký thành công!");
        setTimeout(() => {
          history.push("/login");
        }, 3000);
      })
      .catch((err) => {
        console.log("err", err);
      });
  };
};

export const dangNhapAction = (data) => {
  return (dispatch) => {
    QuanLyNguoiDungServ.dangNhap(data)
      .then((res) => {
        // console.log("res", res);
        message.success("Đăng nhập thành công!");
        setTimeout(() => {
          history.push("/");
        }, 3000);
      })
      .catch((err) => {
        console.log("err", err);
        message.error(`${err.err.response.data.content}`);
      });
  };
};
