import React, { useEffect } from "react";
import { CustomCard } from "@tsamantanis/react-glassmorphism";
import "@tsamantanis/react-glassmorphism/dist/index.css";
import "../../assets/style/circleRating.css";
import { StarOutlined } from "@ant-design/icons";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { layThongTinPhimAction } from "../../redux/actions/quanLyPhimAction";
import moment from "moment";

export default function Detail() {
  const { id } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(layThongTinPhimAction(id));
  }, []);

  const { thongTinChiTietPhim } = useSelector(
    (state) => state.quanLyPhimReducer
  );
  return (
    <div
      style={{
        backgroundImage: `url(${thongTinChiTietPhim.hinhAnh})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        minHeight: 700,
      }}
    >
      <CustomCard
        style={{ minHeight: 700, paddingTop: 150 }}
        effectColor="#04823b" // required
        color="#c0d9cc" // default color is white
        blur={10} // default blur value is 10px
        borderRadius={0} // default border radius value is 10px
      >
        <div className="">
          <div className="grid grid-cols-6">
            <div className="col-start-2">
              <img src={thongTinChiTietPhim.hinhAnh} alt="..." />
            </div>
            <div className="col-span-2 flex items-center">
              <div>
                <h1 className="text-white text-5xl">
                  {thongTinChiTietPhim.tenPhim}
                </h1>
                <p>
                  {moment(thongTinChiTietPhim.ngayKhoiChieu).format(DD.MM.YYYY)}
                </p>
                <p>{thongTinChiTietPhim.moTa}</p>
              </div>
            </div>
            <div className="flex flex-col items-center justify-center">
              <div className="c100 p50 big green">
                <span>50%</span>
                <div className="slice">
                  <div className="bar"></div>
                  <div className="fill"></div>
                </div>
              </div>
              <div className="space-x-2 text-xl text-yellow-600">
                <StarOutlined />
                <StarOutlined />
                <StarOutlined />
                <StarOutlined />
                <StarOutlined />
              </div>
            </div>
          </div>
        </div>
      </CustomCard>
    </div>
  );
}
