import React, { useEffect, useState } from "react";
import { Tabs } from "antd";
import httpServ from "../../../services/http.service";
import ItemTabsPhim from "./ItemTabsPhim";

const { TabPane } = Tabs;

export default function TabsPhim(props) {
  const [arrHeThongRap, setArrHeThongRap] = useState([]);
  useEffect(() => {
    httpServ
      .layThongTinLichChieuHeThongRap()
      .then((res) => {
        console.log("res", res);
        setArrHeThongRap(res.data.content);
      })
      .catch((err) => {
        console.log("err", err);
      });
  }, []);

  const renderHeThongRap = () => {
    return arrHeThongRap.map((heThongRap, index) => {
      return (
        <TabPane
          tab={
            <img src={heThongRap.logo} className="rounded-full" width={60} />
          }
          key={index}
        >
          <Tabs tabPosition={"left"} defaultActiveKey="0" className="mt-20">
            {heThongRap.lstCumRap?.map((cumRap, index) => {
              return (
                <TabPane
                  tab={
                    <img src={cumRap.hinhAnh} className="rounded" width={60} />
                  }
                  key={index}
                >
                  {cumRap.danhSachPhim?.map((rap, index) => {
                    return (
                      <ItemTabsPhim key={index} dsPhim={rap.danhSachPhim} />
                    );
                  })}
                </TabPane>
              );
            })}
          </Tabs>
        </TabPane>
      );
    });
  };
  return (
    <Tabs tabPosition={"left"} defaultActiveKey="0" className="mt-20">
      {renderHeThongRap()}
    </Tabs>
  );
}
