import { SET_DANH_SACH_PHIM, SET_PHIM_DANG_CHIEU } from "../type/phimType";

let initialState = {
  arrFilm: [],
  dangChieu: true,
  sapChieu: true,
};
export const layDanhSachPhimReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_DANH_SACH_PHIM: {
      state.arrFilm = action.payload;
      return { ...state };
    }
    case SET_PHIM_DANG_CHIEU: {
      state.arrFilm = state.arrFilm.filter(
        (phim) => phim.dangChieu === state.dangChieu
      );
      return { ...state };
    }
    default:
      return { ...state };
  }
};
