import React, { useEffect } from "react";

import { Carousel } from "antd";
import { useDispatch, useSelector } from "react-redux";
import { getCarouselAction } from "../../../redux/actions/carouselAction";
import axios from "axios";
import { DOMAIN, TOKEN_CYBERSOFT } from "../../../util/configURL/constanURL";
import "./HomeCarousel.css";

export default function HomeCarousel() {
  const dispatch = useDispatch();
  const { arrCarousel } = useSelector((state) => state.carouselReducer);
  useEffect(() => {
    dispatch(getCarouselAction());
  }, []);

  const contentStyle = {
    height: "100vh",
    width: "100vw",
    color: "#fff",
    lineHeight: "160px",
    textAlign: "center",
    background: "#364d79",
  };

  const renderCarousel = () => {
    return arrCarousel.map((item, index) => {
      return (
        <div key={index}>
          <img
            style={{ width: "100vw", height: "800px", objectFit: "cover" }}
            src={item.hinhAnh}
            alt="123"
          />
        </div>
      );
    });
  };
  return <Carousel autoplay>{renderCarousel()}</Carousel>;
}
