import React from "react";
import { Tabs } from "antd";
import { useSelector } from "react-redux";
import moment from "moment";
import "moment/locale/vi";
const { TabPane } = Tabs;

export default function DetailLichChieuPhim() {
  const { thongTinChiTietPhim } = useSelector(
    (state) => state.quanLyPhimReducer
  );
  console.log("thongTinChiTietPhim", thongTinChiTietPhim);

  const capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  const renderHeThongRapLichChieu = () => {
    return thongTinChiTietPhim.heThongRapChieu?.map((HTR, indexHTR) => {
      return (
        <TabPane
          tab={
            <img
              style={{ width: 50, height: 50, objectFit: "cover" }}
              src={HTR.logo}
            />
          }
          key={indexHTR}
        >
          <Tabs tabPosition={"left"}>
            {HTR.cumRapChieu?.map((cumRap, indexCumRap) => {
              return (
                <div>
                  <TabPane
                    key={indexCumRap}
                    tab={
                      <div className="flex">
                        <img
                          src={cumRap.hinhAnh}
                          alt={cumRap.tenCumRap}
                          style={{ width: 60, height: 60, borderRadius: 6 }}
                        />
                        <div style={{ lineHeight: 1, marginLeft: 10 }}>
                          <h1 className="text-green-700 text-xl text-left">
                            {cumRap.tenCumRap}
                          </h1>
                          <p className="text-green-500 text-left mt-2">
                            {cumRap.diaChi}
                          </p>
                        </div>
                      </div>
                    }
                  >
                    {cumRap.lichChieuPhim?.map((lichChieu, indexLichChieu) => {
                      return (
                        <div key={indexLichChieu}>
                          <p className="text-green-700 text-xl ">
                            {capitalizeFirstLetter(
                              moment(lichChieu.ngayChieuGioChieu).format(
                                "dddd, DD-MM-YYYY"
                              )
                            )}
                          </p>
                          <button className="text-green-700 border-2 border-green-400 rounded px-4 py-1">
                            {moment(lichChieu.ngayChieuGioChieu).format(
                              "hh:mm "
                            )}
                          </button>
                        </div>
                      );
                    })}
                  </TabPane>
                </div>
              );
            })}
          </Tabs>
        </TabPane>
      );
    });
  };

  return (
    <Tabs tabPosition={"left"} defaultActiveKey={0}>
      {renderHeThongRapLichChieu()}
    </Tabs>
  );
}
