import { cloneElement } from "react";
import {
  SET_DANH_SACH_PHIM,
  SET_PHIM_DANG_CHIEU,
  SET_PHIM_SAP_CHIEU,
} from "../type/phimType";

let initialState = {
  arrFilm: [],
  arrFilmDefault: [],
};
export const layDanhSachPhimReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_DANH_SACH_PHIM: {
      state.arrFilm = action.payload;
      state.arrFilmDefault = state.arrFilm;
      return { ...state };
    }
    case SET_PHIM_DANG_CHIEU: {
      state.arrFilm = state.arrFilm.filter((phim) => phim.dangChieu);
      state = clonState;
      return { ...state };
    }
    case SET_PHIM_SAP_CHIEU: {
      state.arrFilm = state.arrFilm.filter(
        (phim) => phim.sapChieu && phim.dangChieu === false
      );
      return { ...state };
    }
    default:
      return { ...state };
  }
};
